import React, { useState } from 'react';
import { Row, Col, Button, Modal, Form } from 'react-bootstrap';

import "./modal.css"

const VeriEkle = () => {

    const [form, setForm] = useState({})
    const [errors, setErrors] = useState({})

    const setField = (field, value) => {
        setForm({
            ...form,
            [field]: value
        })

        if (!!errors[field]) setErrors({
            ...errors,
            [field]: null
        })
    }

    const handleSubmit = e => {
        e.preventDefault()
        // get our new errors
        const newErrors = findFormErrors()
        // Conditional logic:
        if (Object.keys(newErrors).length > 0) {
            // We got errors!
            setErrors(newErrors)
        } else {
            // No errors! Put any logic here for the form submission!
            alert('Thank you for your feedback!')
        }
    }

    const findFormErrors = () => {
        const { ogrNo } = form
        const newErrors = {}
        // name errors
        if (!ogrNo || ogrNo === '' || ogrNo.length < 3) newErrors.name = 'cannot be blank!'
        else if (ogrNo.length > 12) newErrors.name = 'ogrNo is too long!'

        return newErrors
    }

    return (
        <>
            <Modal >
                <Modal.Header closeButton>
                    <Modal.Title id="exampleModalLabel">"title"</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form class="needs-validation" novalidate>

                        <Form.Group>
                            <Form.Label>Name</Form.Label>
                            <Form.Control
                                type='text'
                                onChange={e => setField('name', e.target.value)}
                                isInvalid={!!errors.name}
                            />
                            <Form.Control.Feedback type='invalid'>{errors.name}</Form.Control.Feedback>
                        </Form.Group>


                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button type="button" class="btn btn-secondary" data-dismiss="modal">Kapat</Button>
                    {"asd" === "" ? '' : <Button type='submit' onClick={handleSubmit}>Submit</Button>}
                </Modal.Footer>
            </Modal>
        </>
    );
}
