import React, { useState } from 'react';
import { Row, Col, Button, Modal, Form } from 'react-bootstrap';

import "./modal.css"

const VeriEkle = () => {

    const [form, setForm] = useState({})
    const [errors, setErrors] = useState({})

    const setField = (field, value) => {
        setForm({
            ...form,
            [field]: value
        })

        if (!!errors[field]) setErrors({
            ...errors,
            [field]: null
        })
    }

    const handleSubmit = e => {
        e.preventDefault()
        // get our new errors
        const newErrors = findFormErrors()
        // Conditional logic:
        if (Object.keys(newErrors).length > 0) {
            // We got errors!
            setErrors(newErrors)
        } else {
            // No errors! Put any logic here for the form submission!
            alert('Thank you for your feedback!')
        }
    }

    const findFormErrors = () => {
        const { ogrNo } = form
        const newErrors = {}
        // name errors
        if (!ogrNo || ogrNo === '' || ogrNo.length < 3) newErrors.name = 'cannot be blank!'
        else if (ogrNo.length > 12) newErrors.name = 'ogrNo is too long!'

        return newErrors
    }

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
        <>
            <Button onClick={handleShow} variant="primary" data-toggle="modal" data-target="#studentAdd"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-person-plus"
                viewBox="0 0 16 16">
                <path
                    d="M6 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0zm4 8c0 1-1 1-1 1H1s-1 0-1-1 1-4 6-4 6 3 6 4zm-1-.004c-.001-.246-.154-.986-.832-1.664C9.516 10.68 8.289 10 6 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10z" />
                <path fillRule="evenodd"
                    d="M13.5 5a.5.5 0 0 1 .5.5V7h1.5a.5.5 0 0 1 0 1H14v1.5a.5.5 0 0 1-1 0V8h-1.5a.5.5 0 0 1 0-1H13V5.5a.5.5 0 0 1 .5-.5z" />
            </svg>
            </Button>
            <Modal show={show} onHide={handleClose} >
                <Modal.Header closeButton>
                    <Modal.Title id="studentAdd">"title"</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form class="needs-validation" noValidate>
                        <Form.Group>
                            <Form.Label>ogrNo</Form.Label>
                            <Form.Control
                                type='text'
                                onChange={e => setField('ogrNo', e.target.value)}
                                isInvalid={!!errors.name}
                            />
                            <Form.Control.Feedback type='invalid'>{errors.ogrNo}</Form.Control.Feedback>
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>Rating</Form.Label>
                            <Form.Control
                                type='number'
                                onChange={e => setField('rating', e.target.value)}
                                isInvalid={!!errors.rating}
                            />
                            <Form.Control.Feedback type='invalid'>{errors.rating}</Form.Control.Feedback>
                        </Form.Group>


                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button type="button" class="btn btn-secondary" data-dismiss="modal">Kapat</Button>
                    {"asd" === "" ? '' : <Button type='submit' onClick={handleSubmit}>Submit</Button>}
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default VeriEkle;
