import { Container, Button, Modal, Form } from 'react-bootstrap';
import { BsExclamationTriangleFill } from "react-icons/bs";

import "./modal.css"

const VeriSil = () => {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
        <>
            <Button onClick={handleShow} type="button" data-toggle="modal" data-target={`#`} variant="danger">Sil</Button>
            <Modal show={show} onHide={handleClose} >
                <Modal.Header closeButton>
                    <BsExclamationTriangleFill style={{ color: "red" }} />
                    <Modal.Title id="studentDelete" style={{ paddingLeft: "20px" }}>Sil</Modal.Title>
                </Modal.Header>
                <Modal.Body>

                </Modal.Body>
                <Modal.Footer>
                    <Button type="button" variant="secondary" data-dismiss="modal">Kapat</Button>
                    <Button type='submit'>Sil</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default VeriSil;
