import { Container, Row, Col } from 'react-bootstrap';
import VeriSil from './veriSil';
import VeriEkle from './veriEkle';
import { BsFillPersonXFill } from 'react-icons/bs';

function TableRow({ data, callBack }) {
    const depts = {
        "1": "Bilgisayar Müh.",
        "2": "Elektrik-Elektronik Müh.",
        "3": "Endüstri Müh.",
        "4": "İnşaat Müh.",
    };
    return <Row>
        <Col>{data.firstname} {data.lastname}</Col>
        <Col className='number'>{data.number}</Col>
        <Col className='dept'>{depts[data.departmant]}</Col>
        <Col>
            <VeriSil onClick={callBack} id={data.tid} fname={data.firstname} lname={data.lastname} />
            <VeriEkle callBack={callBack} child={<p>Düzenle</p>} detail={null} tid={data.tid} />
            <VeriEkle callBack={callBack} child={<p>Detay</p>} detail={0} tid={data.tid} />
        </Col>
    </Row>
}

export default function Elements({ children, bottom, top, callBack }) {
    return <Container className='elements' fluid style={{ padding: '0px' }}>
        {children.slice(bottom - 1, top).map((e, idx) => <TableRow key={idx} data={e} callBack={callBack} />)}
    </Container>
}
