import { Container, Row, Col, Button } from 'react-bootstrap';

function CustomRow({ data }) {
    const depts = {
        "1": "Bilgisayar Müh.",
        "2": "Elektrik-Elektronik Müh.",
        "3": "Endüstri Müh.",
        "4": "İnşaat Müh.",
    };
    return <Row>
        <Col>{data.firstname} {data.lastname}</Col>
        <Col className='number'>{data.number}</Col>
        <Col className='dept'>{depts[data.departmant]}</Col>
        <Col>
            <Button type="button" data-toggle="modal" data-target={`#studentDelete-${data.tid}`} variety="danger">Sil</Button>
            <Button type="button" data-toggle="modal" data-target={`#studentUpdate-${data.tid}`} variety="primary">Düzenle</Button>
            <Button type="button" data-toggle="modal" data-target={`#studentDetail-${data.tid}`} variety="success">Detay</Button>
        </Col>
    </Row>;
}

export default function Elements({ children, bottom, top }) {
    return <Container className='elements' fluid style={{ padding: '0px' }}>
        {children.slice(bottom - 1, top).map((e, idx) => <CustomRow key={idx} data={e} />)}
    </Container>
}
