import React, { useState, useEffect } from "react";
import { Container, Row, Col } from 'react-bootstrap';
import "./tableCard.css"
import Elements from "./elements/elements";

export default function TableCard() {
    const [state, setState] = useState({
        children: [],
        currentPage: 1,
        pageLimit: 10,
        pageCount: 1,
        bottom: 0,
        top: 0
    });

    const updateState = (newState) => {
        setState(previous => ({
            ...previous,
            newState
        }))
    }

    useEffect(() => {
        async function initialize() {
            let list = []
            await fetch('http://localhost:8000/students/', {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
            })
                .then(async (response) => {
                    let res = await response.json()
                    console.log(res)
                    res.map(el => {
                        let data = {
                            "tid": el.id,
                            "firstname": el.fname,
                            "lastname": el.lname,
                            "number": el.num,
                            "departmant": el.dept,
                            "placeOfBirth": el.pob,
                            "dateOfBirth": el.dob,
                        }
                        list = [...list, data]
                    })
                })
            updateState({ list: list })
        }
        initialize();
    }, [])

    return <Container className="text-center table" fluid>
        <Row className="table-header">
            <Col>İsim Soyisim</Col>
            <Col className="number">Öğrenci Numarası</Col>
            <Col className="dept">Bölüm</Col>
            <Col>Yetkiler</Col>
        </Row>

        <Elements children={state.children} bottom={state.bottom} top={state.top} />

        <Row className="table-footer">
            <Col className="pageNumber">
                <span className="bold">{state.children.length}</span> öğrenciden <span className="bold"> {state.bottom}-{state.top}</span> arası gösteriliyor
            </Col>
            <Col className="paginationNumbers" md="auto">
                {Array.from({ length: state.pageCount }).map((_, idx) => {
                    return <div key={idx}
                        className={`${(state.currentPage === idx + 1) ? 'active' : ''}`}
                        onClick={() => updateState({ current: idx + 1 })}>
                        {idx + 1}</div>
                })}
            </Col>
            <Col className="pageCounter">
                <div onClick={() => updateState({ limit: 5 })}>5</div>
                <div onClick={() => updateState({ limit: 8 })}>8</div>
                <div onClick={() => updateState({ limit: 10 })}>10</div>
            </Col>
        </Row>
    </Container>
}
