import React, { useState, useEffect } from "react";
import { Container, Row, Col } from 'react-bootstrap';
import "./tableCard.css"
import Elements from "./elements/elements";

export default function TableCard() {
    const [state, setState] = useState({
        children: [],
        currentPage: 1,
        pageLimit: 10,
        pageCount: 1,
        bottom: 0,
        top: 0
    });

    const updateIndices = (newState) => {
        setState(p => ({
            ...p,
            newState
        }))
    }

    useEffect(() => {
        async function initialize() {
            let list = []
            await fetchAsync('http://localhost:8000/students', 'GET', null)
                .then(async (response) => {
                    let res = await response.json()
                    res.map(el => {
                        let data = {
                            "tid": el.id,
                            "firstname": el.fname,
                            "lastname": el.lname,
                            "number": el.num,
                            "departmant": el.dept,
                            "placeOfBirth": el.pob,
                            "dateOfBirth": el.dob,
                        }
                        list = [...list, data]
                    })
                })
            updateIndices({ list: list })
        }
        initialize();
    }, [])

    return <Container className="text-center table" fluid>
        <Row className="table-header">
            <Col>İsim Soyisim</Col>
            <Col className="number">Öğrenci Numarası</Col>
            <Col className="dept">Bölüm</Col>
            <Col>Yetkiler</Col>
        </Row>

        <Elements children={state.children} bottom={state.bottom} top={state.top} />

        <Row className="table-footer">
            <Col className="pageNumber">
                <span className="bold">{state.children.length}</span> öğrenciden
                <span className="bold"> {state.bottom}-{state.top}</span> arası gösteriliyor
            </Col>
            <Col className="paginationNumbers" md="auto">
                {Array.from({ length: state.pageCount }).map((_, idx) => {
                    return <div key={idx}
                        className={`${(state.currentPage === idx + 1) ? 'active' : ''}`}
                        onClick={() => updateIndices({ current: idx + 1 })}>
                        {idx + 1}</div>
                })}
            </Col>
            <Col className="pageCounter">
                {[5, 8, 10].map((e, idx) => {
                    return <div key={idx} onClick={() => updateIndices({ limit: e })}>{e}</div>
                })}
            </Col>
        </Row>
    </Container>
}

var bigMedia = window.matchMedia("(max-width: 992px)")
bigMedia.addEventListener("change", () => {
    const dept = document.querySelectorAll(".table .dept")
    dept.forEach(e => {
        e.style.display = !bigMedia.matches ? "flex" : "none"
    });
})

var smallMedia = window.matchMedia("(max-width: 768px)")
smallMedia.addEventListener("change", () => {
    const number = document.querySelectorAll(".table .number")
    number.forEach(e => {
        e.style.display = !smallMedia.matches ? "flex" : "none"
    });
})
